import React, { Fragment, useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux'
import { Link } from "react-router-dom";
import "../../styles/App.css";
import { updateCart, removeFromCart } from  '../../lib/actions'

const Row = (props) => {
    const { id, quantity, details } = props.item
    const item = details
    const [qty, setQty] = useState(quantity);
    const dispatch = useDispatch()
    const update = (action) => {
      if (action === 'increment') { setQty(qty + 1) }
      if (action === 'decrement') { setQty(qty - 1) }
    }
    const remove = id => {
      dispatch(removeFromCart(id))
    }
    useEffect(() => {
      dispatch(updateCart(id, qty))
    }, [qty])
    return (
      <tr>
        <td>
          <img
            width="70"
            height="70"
            src={process.env.PUBLIC_URL + `/assets/${item.category}/${item.image}`}
            alt={item.name}
          />
        </td>
        <td>{item.ref}</td>
        <td>â‚¬{item.price}</td>
        <td>
          <div className="btn-group" role="group" aria-label="Basic example">
            <button
              type="button"
              className="btn btn-secondary"
              onClick={() => {
                if (qty > 1) {update('decrement') }
              }}
              >
              -
            </button>
            <span className="btn btn-light">{qty}</span>
            <button
              type="button"
              className="btn btn-secondary"
              onClick={() =>  {update('increment')}}
              >
              +
            </button>
          </div>
        </td>
        <td>â‚¬{(quantity *  item.price).toFixed(2)}</td>
        <td>
          <button
            type="button"
            className="btn btn-danger remove"
             onClick={() => remove(id)}>
            X
          </button>
        </td>
      </tr>
    );
}

const Table = ({ items }) => {
    return (
      <table>
        <tr>
          <th width="200">Product</th>
          <th width="80">Reference</th>
          <th width="150">Price</th>
          <th width="150">Quantity</th>
          <th width="200">Total</th>
        </tr>
        {items.map(item => {
          return(<Row item={item}/>)
        })}
      </table>
    );
  }

export const CartPage = () => {
    const items = useSelector(state => state.items)
    const [subTotal, setSubTotal] = useState(0.00)
    const [total, setTotal] = useState(0.00)
    const shipping = 10.00

    useEffect(() => {
      let totals = items.map(item => {
        return item.quantity * item.details.price
      })
      setSubTotal(totals.reduce((item1, item2) => item1 + item2, 0))
      setTotal(subTotal + shipping)
      console.log(`Subtotal:  â‚¬${subTotal} `)
      console.log(`You have ${items.length} in your cart`)
    }, [items, subTotal, total])
    return (
      <Fragment>
        <div className="container">
        <div className="row">
          <div className="col-sm cart">
              <Table items={items}/>
          </div>

          <div className="col-sm-3 order-summary">
            <ul className="list-group">
              <li className="list-group-item">Order Summary</li>

              <li className="list-group-item">
                <ul className="list-group flex">
                  <li className="text-left">Subtotal</li>
                  <li className="text-right">â‚¬{subTotal.toFixed(2)}</li>
                </ul>
                <ul className="list-group flex">
                  <li className="text-left">shipping</li>
                  <li className="text-right">â‚¬{shipping.toFixed(2)}</li>
                </ul>
                <ul className="list-group flex">
                  <li className="coupon crimson">
                    <small> >> Add Coupon Code</small>
                  </li>
                </ul>
              </li>

              <li className="list-group-item ">
                <ul className="list-group flex">
                  <li className="text-left">Total</li>
                    <li className="text-right">â‚¬{subTotal == 0.00 ? "0.00" : total.toFixed(2)}</li>
                </ul>
              </li>
            </ul>
            <button
              type="button"
              className="btn btn-light btn-lg btn-block checkout bg-crimson"
              disabled="true"
            >
              <a href="#" className="white">
                Checkout
              </a>
            </button>
          </div>
        </div>
        </div>
      </Fragment>
    );
}
