import React, { createContext, useState } from 'react'

export const UserProfileContext = createContext({
    firstName: "", 
    lastName: "", 
    email: "", 
    address: "",
    zipCode: "", 
    city: "", 
    setUserProfileContext : info => {}          //Mise à jour du UserProfile àpd composant 
})

const UserProfileContextProvider = ({ children }) => {
    const userProfileState = {
        firstName: "", 
        lastName: "", 
        email: "", 
        address: "",
        zipCode: "", 
        city: "", 
        setUserProfileContext : info => 
            setUserProfile(prevState => ({  //Mise à jour de l'état local du provider, ne modifie que les variables qui ont été modifiées 
            ...prevState, 
            firstName : info.firstName,
            lastName : info.lastName,
            email : info.email, 
            address : info.address,
            zipCode : info.zipCode, 
            city : info.city 
        }))
    }
    const [userProfile, setUserProfile] = useState(userProfileState)
    return (
        <UserProfileContextProvider value={userProfile} >{ children }</UserProfileContextProvider> //userProfile valeur local 
    )
}
export default UserProfileContextProvider;
